% topottest% [topo_tmat, mat3d_1, mat3d_2] = topottest(filemat1, filemat2, bslvec,% outname)function [topo_tmat, mat3d_1, mat3d_2] = topottest(filemat1, filemat2, bslvec, outname)if nargin < 4;     outname = []; end[a,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,...    NChanExtra,TrigPoint,HybridFactor,HybridDataCell,DataTypeVal]=ReadAvgFile(deblank(filemat1(1,:))); mat3d_1 = zeros(size(a,1), size(a,2), size(filemat1,1) ); mat3d_2 = zeros(size(a,1), size(a,2), size(filemat1,1) ); for subject = 1:size(filemat1,1);        a = ReadAvgFile(deblank(filemat1(subject,:)));        if ~isempty (bslvec)    mat3d_1(:,:, subject) = bslcorr(a, bslvec);    else       mat3d_1(:,:, subject) = a;    end              b = ReadAvgFile(deblank(filemat2(subject,:)));           if ~isempty (bslvec)        mat3d_2(:,:, subject) = bslcorr(b, bslvec);       else          mat3d_2(:,:, subject) = b;       endend[dummy, dummy, dummy, stats] = ttest(mat3d_1, mat3d_2, 0.05, [], 3); topo_tmat = stats.tstat;  SaveAvgFile(outname, topo_tmat,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra,TrigPoint,HybridFactor);         